// Extract plain object for easier template usage
const userData = computed(() => user?.value || {});

const roles = computed(() => userData.value.roles || []);

const isAdmin = computed(() =>
  roles.value.some(r => r.name === "Admin")
);

const isPetugasIT = computed(() =>
  roles.value.some(r => r.name === "Petugas IT")
);

const isEndUser = computed(() =>
  roles.value.some(r => r.name === "End User")
);


